
aws dynamodb create-table --table-name Tasks --attribute-definitions AttributeName=board_id,AttributeType=S AttributeName=id,AttributeType=S --key-schema AttributeName=board_id,KeyType=HASH AttributeName=id,KeyType=RANGE --provisioned-throughput ReadCapacityUnits=5,WriteCapacityUnits=5 --endpoint-url http://localhost:8000
aws dynamodb update-table --table-name Tasks --attribute-definitions AttributeName=board_id,AttributeType=S AttributeName=created_at,AttributeType=S --global-secondary-index-updates "Create={IndexName=CreatedAtIndex,KeySchema=[{AttributeName=board_id,KeyType=HASH},{AttributeName=created_at,KeyType=RANGE}],Projection={ProjectionType=ALL},ProvisionedThroughput={ReadCapacityUnits=5,WriteCapacityUnits=5}}"  --endpoint-url http://localhost:8000
aws dynamodb update-table --table-name Tasks --attribute-definitions AttributeName=board_id,AttributeType=S AttributeName=priority,AttributeType=N --global-secondary-index-updates "Create={IndexName=PriorityIndex,KeySchema=[{AttributeName=board_id,KeyType=HASH},{AttributeName=priority,KeyType=RANGE}],Projection={ProjectionType=ALL},ProvisionedThroughput={ReadCapacityUnits=5,WriteCapacityUnits=5}}"  --endpoint-url http://localhost:8000